// Minimal local wrapper for marked (recommend replacing with official minified file)
// This file intentionally loads the CDN as a fallback only for development.
(function(){
  if (typeof window.marked === 'undefined') {
    var s = document.createElement('script');
    s.src = 'https://cdn.jsdelivr.net/npm/marked/marked.min.js';
    s.defer = true;
    document.head.appendChild(s);
  }
})();
